mapper.select:
SELECT SUBSTR(r_code,INSTR(r_code,'/')+1, 10) r_date, m_code, title, content, address, r_code
FROM resume
WHERE m_code = 'p' || #{id}

mapper.update:
UPDATE resume
SET title = #{title}, content = #{content}, address = #{address}, education = #{education}
WHERE r_code = #{r_code}


	UPDATE resume_file
	SET file_name = #{file_name}
	WHERE r_code = #{r_code}
