mapper.update:
update post
set viewcnt = viewcnt + 1
where p_code = #{p_code}

		UPDATE post SET 
					title = #{title}, 
					p_content = #{p_content}
    	WHERE p_code = #{p_code}

mapper.delete:
declare 
	begin 
	delete from post_file where p_code = #{p_code};
	delete from reply where p_code = #{p_code};
	delete from post where p_code = #{p_code};
end;

mapper.select:
SELECT * FROM post_file
WHERE p_code = #{p_code}
ORDER BY upload_time desc

mapper.insert:
INSERT INTO post_file (
	fullname, p_code
) VALUES (
	#{fullName}, #{p_code}
)
